let inputMoney=document.querySelector("#money"),inputCoin=document.querySelector("#coin"),ExtrValue=document.querySelector(".currency-select");inputMoney.addEventListener("input",()=>{let e=new XMLHttpRequest;e.open("GET","https://blockchain.info/ru/ticker",!0),e.setRequestHeader("Content-type","application/json; charset=utf-8"),e.send(),e.addEventListener("load",()=>{if(200===e.status){let t=JSON.parse(e.response);console.log(t),"RUB"==ExtrValue.value?inputCoin.value=(+inputMoney.value/t.RUB.buy).toFixed(8):"USD"==ExtrValue.value?inputCoin.value=(+inputMoney.value/t.USD.buy).toFixed(8):"AUD"==ExtrValue.value&&(inputCoin.value=(+inputMoney.value/t.AUD.buy).toFixed(8))}else console.log("Что-то пошло не так")})});